import React, { useState, useEffect } from "react";
import "./Row.css";
import axios from "axios";

const API_KEY = "fad866c27c86c4b55dbd8f0e152453c6";
const lang = "language=en-US";

const baseUrl = "https://api.themoviedb.org/3";
const imageBaseUrl = "https://image.tmdb.org/t/p/";
const file_path = "k68nPLbIST6NP96JmTxmZijEvCA.jpg";

const Row = ({ title, fetchParam }) => {
  const [movies, setMovies] = useState([]);
  console.log(fetchParam);

  useEffect(() => {
    async function fetchData() {
      try {
        const res = await axios.get(
          `${baseUrl}/${fetchParam}?api_key=${API_KEY}&${lang}`
        );

        setMovies(res.data.results);
        return movies;
      } catch (err) {
        console.log(err);
      }
    }
    fetchData();
  }, );

  //console.log("movies:", movies);
  return (
    <div className="row">
      <h1>{title}</h1>
      <div className="row_posters">
        {movies &&
          movies.map(movie => {
            return (
              <img
                key={movie.id}
                src={`${imageBaseUrl}/w200/${file_path}`}
                alt={movie.name || movie.title}
                className="row_poster"
              />
            );
          })}
      </div>
    </div>
  );
};

export default Row;
